import math

def is_perfect_square(n):
    return math.isqrt(n)**2 == n

def find_greatest_n(limit):
    for n in reversed(range(1, limit+1)):
        left_sum = sum(i**2 for i in range(1, n+1))
        right_sum = sum(i**2 for i in range(n+1, 2*n+1))
        if is_perfect_square(left_sum * right_sum):
            return n
    return None

