    i = 1
    while i <= math.sqrt(n):
        # if i is a factor of n
        if n % i == 0:
            # if both divisors are same then add one
            if n/i == i:
                if i % 3 == 0:
                    count += 1
            else:
                # else add 2
                if i % 3 == 0:
                    count += 1
                if (n/i) % 3 == 0:
